import { useState, useEffect } from 'react';
import '../CSS/App.css';
import { Navbar } from './Navbar';
import { Header } from './Header'
import '../CSS/CallToAction.css'

export const CallToAction = () => {
  return (
    <section className="section-container">
      <div id="call-to-action">
        <div className='ghost'></div>
        <h1 id="cta-title">Are you ready to use a smarter surveillance system?</h1>
        <button>Get in touch</button>
        <div className='ghost'></div>
      </div>
    </section>
  )
}

function App() {
  const [tabletDisplay, setTabletDisplay] = useState(false)
  const [screenWidth, setScreenWidth] = useState(window.innerWidth)
  useEffect(() => {
    console.log("resized screen width to: ", screenWidth)
    setTabletDisplay(screenWidth < 700)
  })
  useEffect(() => {
    const handleResize = () => setScreenWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  return (
    <div className="App">
      <Navbar tabletDisplay={tabletDisplay} />
      <Header />
      <CallToAction />
      <footer></footer>
    </div >
  );
}

export default App;
