// src/components/Navbar.jsx
import React, { useState, useEffect } from 'react';
import '../CSS//Navbar.css';

export const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Detect screen size changes
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
      // Close menu when switching to desktop
      if (window.innerWidth > 768 && isMenuOpen) {
        setIsMenuOpen(false);
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [isMenuOpen]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <nav>
      <section id="navbar">
        <h1 id="nav-title">Novatus</h1>

        {/* Desktop Menu */}
        <ul id="nav-menu" className="desktop-menu">
          <li>Platform</li>
          <li>Faq</li>
          <li>Careers</li>
        </ul>

        <button className='header-btn std-hover-active' id="service-btn">Contact Us</button>

        {/* Mobile Menu Button */}
        <button
          id="hamburger"
          className={isMenuOpen ? 'open' : ''}
          onClick={toggleMenu}
          aria-label={isMenuOpen ? "Close menu" : "Open menu"}
        >
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </button>
      </section>

      {/* Mobile Dropdown Menu */}
      {isMenuOpen && (
        <div id="mobile-menu">
          <ul>
            <li onClick={closeMenu}>Platform</li>
            <li onClick={closeMenu}>Faq</li>
            <li onClick={closeMenu}>Careers</li>
            <li>
              <button
                className='header-btn std-hover-active'
                onClick={closeMenu}
              >
                Contact Us
              </button>
            </li>
          </ul>
        </div>
      )}
    </nav>
  )
}
