// src/components/Navbar.jsx
import React, { useState, useEffect } from 'react';
import '../CSS/Navbar.css';

export const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Detect screen size changes
  useEffect(() => {
    const handleResize = () => {
      const nowMobile = window.innerWidth <= 768;
      setIsMobile(nowMobile);
      // Close menu when switching to desktop
      if (!nowMobile && isMenuOpen) {
        setIsMenuOpen(false);
      }
    };

    handleResize();
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [isMenuOpen]);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <nav>
      <section id="navbar">
        <h1 id="nav-title">Novatus</h1>

        {/* Desktop menu with dropdown on hover */}
        <div className="dropdown-container" onMouseLeave={() => !isMobile && setIsMenuOpen(false)}>
          <div
            className="menu-trigger"
            onClick={isMobile ? toggleMenu : undefined}
            onMouseEnter={() => !isMobile && setIsMenuOpen(true)}
          >
            <span>Menu</span>
            <svg className="dropdown-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M6 9L12 15L18 9" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
            </svg>
          </div>

          {(isMenuOpen || !isMobile) && (
            <ul className={`dropdown-content ${isMenuOpen ? 'open' : ''}`}>
              <li onClick={closeMenu}>Platform</li>
              <li onClick={closeMenu}>Faq</li>
              <li onClick={closeMenu}>Careers</li>
            </ul>
          )}
        </div>

        <button className='header-btn std-hover-active' id="service-btn">Contact Us</button>

        {/* Mobile menu button (hamburger) */}
        <div
          id="hamburger"
          className={isMenuOpen ? 'open' : ''}
          onClick={toggleMenu}
          aria-label={isMenuOpen ? "Close menu" : "Open menu"}
        >
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </div>
      </section>
    </nav>
  )
}
