import { useState, useEffect } from 'react';
import '../CSS/Navbar.css'

const classicMenu = () => {
  return (
    <ul id="nav-menu-classic">
      <li>Platform</li>
      <li>Faq</li>
      <li>Careers</li>
    </ul>
  )
}

export const Navbar = () => {
  const [tabletDisplay, setTabletDisplay] = useState(false)
  const [screenWidth, setScreenWidth] = useState(window.innerWidth)
  useEffect(() => {
    console.log("resized screen width to: ", screenWidth)
    setTabletDisplay(screenWidth < 768)
  })
  useEffect(() => {
    const handleResize = () => setScreenWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  return (
    <nav >
      <section id="navbar">
        <h1 id="nav-title" > Novatus</h1 >
        {tabletDisplay ? null : classicMenu}
        <button className='header-btn std-hover-active' id="service-btn">Get in touch</button>
      </section>
    </nav >
  )
}
