import { useState, useEffect } from 'react';
import '../CSS/Navbar.css'

const ClassicMenu = () => {
  return (
    <ul className='nav-menu' id="nav-menu-classic">
      <li className="std-hover-active">Platform</li>
      <li className="std-hover-active">Faq</li>
      <li className="std-hover-active">Careers</li>
    </ul>
  )
}

const TabletMenu = () => {
  return (
    <details className='nav-menu' id="nav-menu-tablet">
      <summary className="std-hover-active">Menu</summary>
      <ul>
        <li className="std-hover-active">Platform</li>
        <li className="std-hover-active">Faq</li>
        <li className="std-hover-active">Careers</li>
      </ul>
    </details>
  );
};

export const Navbar = () => {
  const [tabletDisplay, setTabletDisplay] = useState(false)
  const [screenWidth, setScreenWidth] = useState(window.innerWidth)
  useEffect(() => {
    console.log("resized screen width to: ", screenWidth)
    setTabletDisplay(screenWidth < 768)
  })
  useEffect(() => {
    const handleResize = () => setScreenWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  return (
    <nav >
      <section id="navbar">
        <h1 id="nav-title" > Novatus</h1 >
        {tabletDisplay ? <TabletMenu /> : <ClassicMenu />}
        <button className='header-btn std-hover-active' id="service-btn">Get in touch</button>
      </section>
    </nav >
  )
}
